import 'dart:ui';import 'package:flutter/material.dart';import 'package:mintness/style.dart';import 'custom_button.dart';class AcceptDeclineAlertDialog extends StatefulWidget {  Function onAccept;  String title;  String text;  AcceptDeclineAlertDialog(this.onAccept, this.title, this.text);  @override  _AcceptDeclineAlertDialogState createState() =>      _AcceptDeclineAlertDialogState();}class _AcceptDeclineAlertDialogState extends State<AcceptDeclineAlertDialog> {  @override  Widget build(BuildContext context) {    return BackdropFilter(        filter: ImageFilter.blur(sigmaX: 6, sigmaY: 6),        child: AlertDialog(          shape: RoundedRectangleBorder(              borderRadius: BorderRadius.all(Radius.circular(32.0))),          title: Row(            mainAxisAlignment: MainAxisAlignment.center,            children: [              Text(                "${widget.title}",                style: TextStyle(color: AppColor.primaryText),              ),            ],          ),          content: Text("${widget.text}",              style: TextStyle(                  color: AppColor.primaryText.withOpacity(0.5), fontSize: 14)),          actions: <Widget>[            Container(                height: 60,                child: Row(              mainAxisSize: MainAxisSize.max,              mainAxisAlignment: MainAxisAlignment.center,              children: [                Container(                    width: 100,                    height: 32,                    child: CustomButton.outline(                  text: "Cancel",                  onTap: () {                    Navigator.of(context).pop();                  },                )),                SizedBox(width: 10,),                Container(                    width: 100,                    height: 32,                    child: CustomButton(                  text: "Accept",                  onTap:()=> widget.onAccept(),                )),              ],            )),          ],        ));  }}